                    			<TextBlock Text="{Binding Result}" TextWrapping="NoWrap" Width="76"/>
                    			<TextBlock Text="{Binding User.Login}" TextWrapping="NoWrap"/>
                    		</StackPanel>                    		
                    	</StackPanel>
                    </DataTemplate>
