    <%= form_for(@venue, :remote=>true) do |f| %>
		<div class="field">
			<%= f.label :address %><br />
			<%= f.text_field :address, :class=>"venue_find" %>
		</div>
		<div class="field">
			<%= f.label :city %><br />
