#else
#include <string.h>
#include <dirent.h>
#include <sys/types.h>
#include <unistd.h>
#endif


#define MAXFILEPATH 1024


bool IsDirectory(char* path)
{
#ifdef WIN32
	WIN32_FIND_DATA w32fd;
	HANDLE hFindFile;

	hFindFile = FindFirstFile((PTCHAR)path, &w32fd);

	if(hFindFile == INVALID_HANDLE_VALUE)
	{
		return false;
	}
	return w32fd.dwFileAttributes & (FILE_ATTRIBUTE_DIRECTORY);	
#else
	struct stat finfo;
	stat(path, &finfo);
	return S_ISDIR(finfo.st_mode);
#endif
}

int RD(const char* folderName)
{
        DIR *dir;
        struct dirent *ent;

        dir = opendir(folderName);
        if(dir != NULL)
        {
                while((ent = readdir(dir)) != NULL)
                {
                        if(strcmp(ent->d_name , ".") == 0 ||
                                strcmp(ent->d_name, "..") == 0)
                        {
                                continue;
                        }

                        char fileName[MAXFILEPATH];
#ifdef WIN32
						sprintf(fileName,"%s%c%s", folderName, '\\', ent->d_name);                        
#else
						sprintf(fileName,"%s%c%s", folderName, '/', ent->d_name);
#endif
						if(IsDirectory(fileName))
						{
	                        RD(fileName);
						}
						else
						{
							unlink(fileName);
						}
                }

