#include <stdio.h>
#include <stdlib.h>
#include <sndfile.h>
#include <math.h>
#include <stdbool.h>
 

#define N 8


#define CONST_PI   3.1415926535897932384626433832
		    


#define TRIG_VARS                   \
long TLen,TNdx;int TDir;             \
double wr, wi, wpr, wpi,proot, root;

#define INIT_TRIG(LENGTH,DIR)    		\
TNdx=0;TLen=(LENGTH);TDir=(DIR);    	\
wr=1.0;wi=0.0;          	\
wpr=sin(2*CONST_PI/((LENGTH)*2.0)); 	\
wpr=-2.0*wpr*wpr;         	\
wpi=sin(2*CONST_PI/(LENGTH))*(DIR); \
printf("LENGTH is %d\n",LENGTH);\
proot = wr + wi;   \
root = wpr + wpi;  \

#define NEXT_TRIG_POW               			\
if (((++TNdx)&15)==0) {            		\
	double Angle=(CONST_PI*(TNdx))/TLen;      \
	wr=sin(Angle*0.5);         	\
	wr=1.0-2.0*wr*wr;	\
	wi=sin(Angle)*(TDir);      	\
} else {             		   \
double temp;                  \
temp = proot; \
proot = proot*root;   \
proot = proot*temp; \
}  \

void Reverse_Binary( double *a, unsigned long Len);
void FFT_T(double *Data, unsigned long Len, int DIR) ;
	
int main()

{
	
	double data[] = {0,1,2,3,4,5,6,7}; 
	
		Reverse_Binary(data,N);	
		FFT_T(data, N, 1);
				
	return 0;
		
}														

void Reverse_Binary( double *a,unsigned long Len)  
{
	long x, xprim;
	int temp;
	
	for (x=0; x<Len; x++)
	{
		xprim= rev(x,N);
		
		
		if (xprim > x)
		{
			temp = a[x];
			a[x] = a[xprim];
			a[xprim] = temp;
			
		}
		
	}
	
}

void FFT_T(double *Data, unsigned long Len, int DIR) {				
	long Step, HalfStep;
	long b;
	TRIG_VARS;
	
	
	Step = 1;
	
	while (Step < Len) {
		HalfStep = Step;
		Step *= 2;
		
		INIT_TRIG(Step,DIR);
		
		for (b = 0; b < HalfStep; b++) {
			long L,R;
			for (L=b; L<Len; L+=Step) {
				double TRight,TLeft;
				R=L+HalfStep;
				TLeft=Data[L];TRight=Data[R];
				
																	
				TRight  = TRight * proot;
				Data[L] = TLeft + TRight;
				Data[R] = TLeft - TRight; 
		}
			
			NEXT_TRIG_POW;
		}
		
	}
}

