    	if (y > 0 && cells[y - 1][x].myType == cells[y][x].myType)
    	{
    		flagMatchingCells(x,y - 1);
    	}
    	if (y < countRows - 1 && cells[y + 1][x].myType == cells[y][x].myType)
    	{
